"{
	dim @time1 as string;
	dim @time2 as string;
	dim @ans as string;
	@time1=time();
	@time1=substr(@time1,0,2)+':'+substr(@time1,2,2)+':'+substr(@time1,4,2);
	commsend_001();
	//下面组测试报文
	$FD12='0000';
	//$FD40='0000000000754118';
	$FD16='9998';
	//////////////////
	callserver_tclt(val(#PCOUNT),val(#PDO));
	showmsg('请稍候...',2);
	sleep(5);
	showmsg('按任意键停止跟踪',2);
	while(true ){
		if(list_tclt()){
			list_show();
			if(getchar(100)!=''){
				showfooter();
				list_work();
				@ans=showmsg('q.退出跟踪 x.查看错误 其他继续');
				if(@ans=='q'){
					break;
				}else if(@ans=='x'){
					call('fview ../tmp/testerror');
					continue;
				}else{
					showmsg('按任意键停止跟踪',2);
					continue;
				}
			}	
		}
	}
	@time2=time();
	@time2=substr(@time2,0,2)+':'+substr(@time2,2,2)+':'+substr(@time2,4,2);
	showmsg('测试结束,开始时间:'+@time1+',结束时间:'+@time2);
  }"
