ITEM.ONCLICK:
"{
	dim @mb1 as string;
	dim @mb2 as string;
	dim @errtex as string;
	if(#TLRNO1!=$TLRNO){
		showmsg('只能采集自己的指纹!');
		return(false);
	}
	showmsg('请按回车键后用左手手指按下指纹输入设备进行采集...');
	showfooter();
	if(not finger_getmb(@mb1)){
		showmsg(@mb1);
	return(false);
	}
	showmsg('请按回车键后用右手手指按下指纹输入设备进行采集...');
	showfooter();
	if(not finger_getmb(@mb2)){
	showmsg(@mb2);
	return(false);
	}
	if(not finger_setmb(#TLRNO1,@mb1,@mb2,@errtex)){
	showmsg(@errtex);
	return(false);
	}
	setconfig($TLRNO+'.FINGER','1');
	runoutput();
	rerun();
	return(true);
  }"
