         //showmsg(#XXZT);
         if(#XXZT == '1')
         {
           #XXZT='签约';
         }
         else if(#XXZT=='2')
         {
           #XXZT='临时解约';
         }
         else if(#XXZT=='*')
         {
           #XXZT='解约';
         }
         else if(#XXZT=='P')
         {
           #XXZT='临时解约';
         }
         else
         {
           showmsg('此账户未签约..');
           return(false);
