"{
	if(#txamt > 500000000000)
	{
		if(msgbox('请检查是否金额输入错误,确认继续,取消重新输入','金额太大提示','ok|cancel') == 1)
		{
			return(false);
		}
	}
	if(#txamt == 0 )
	{
		showmsg('交易金额要大于零!');
		return(false);
	}
	//检查交易金额与项目维护时汇总金额是否一致
	if(#txamt != #HZJE)
	{
		showmsg('交易金额与汇总金额不一致!');
		return(false);
	}
}"

ITEM.ONCLICK:
"{
	dim @AMT as number;
	dim @stramt as string;
	dim @buffer as string;
	dim @file as file;
	dim @remit as string;

	initfd();
	commsend_001();

	//掉短交易1106确定汇路
	$FD16='1106';
	$FD18='pbp';
	$FD34=#BusType;          //业务类型
	$FD64=#BusKind;         //业务种类
	callagnpbp();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	@remit=$FD28;
	//掉短交易1106确定汇路

	initfd();
	commsend_001();

	$FD16 = '1208';
	$FD18 = 'pbp';
	$FD34=#BusType;          //业务类型
	$FD64=#BusKind;         //业务种类
	$FD30=#PayeeAcct;      //收款人账号
	$FD25=#PayeeName;     //收款人名称
	$FD81=#YWXUHAO;       //项目编号
	@AMT=#txamt * 100;
	@stramt=itoa(@AMT, '%d');
	$FD39=@stramt;      //交易金额	
	$FD28=@remit;
	$FD71='1'; //借贷标识
	callagnpbp();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}

	runoutput();
	rerun();
	refresh();
}"

