	$FD59=#RepBnk;
	$FD35=#SrFlag;

	if(delspace(#Brno) != '')
	{
		$FD85=#Brno;
	}

	dim @rcvbuffer as string;
	dim @filename1 as string;
	dim @ret as number;

	callagnpbp();

	@rcvbuffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}

	if(@rcvbuffer =='')
	{
		showmsg('读取回传文件失败！');
		return(false);
	}




	autolist('~'+@rcvbuffer);
	#DOLIST='1';
	goitem('#DOLIST');

	return(true);

}"

