	$FD37 =#CzTel;
	$FD25 =#PayerName;
	$FD31 =#PayeeAcct;
	$FD26 =#PayeeName;

	dim @rcvbuffer as string;
	dim @filename1 as string;
	dim @ret as number;

	@rcvbuffer =callagnpbp();
	@rcvbuffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);


	if(@rcvbuffer =='')
	{
		showmsg('读取回传文件失败！');
		return(false);
	}

	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}


	autolist('~'+@rcvbuffer);
	#DOLIST='1';
	goitem('#DOLIST');

	return(true);

}"

