ITEM.ONCLICK:
	"{
                initfd();
                dim @tota as string;
                dim @column as string;
                dim @where as string;
                commsend_001();
                $FD16='1114';
                $FD18='pbp';
                $FD28='sxps';
                $FD23='0';
                @column='regn_code,regn_level, regn_up'; 
                @where = 'regn_name='+'|'+#Name+'|';
                $FD96=@column;
                $FD76=@where;
                $FD81='sxps_region';
                $FD101='4|1|4';
		callagnpbp();
		if($FD12 !='0000')
		{
			showmsg(geterror());
			return(false);
		}
		@tota=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
                @tota=delspace(@tota);
                #Regncode=strfld(@tota,'|',0);
                #Regnlevel =strfld(@tota,'|',1);
                #Regnup =strfld(@tota,'|',2);
           
                runoutput();
                showall();
                refresh();
		rerun();                 
	}"

