ITEM.ONLOSTFOCUS:
  "{
    		commsend_001();
			$FD25=#OPERNAME;
			$FD28=#CUSTNO;
    		callserver();
    		#ORDERNO=$FD35;
			show(#SHOW,true);
			show(#ORDERNO,true);
			enable(#SHOW,true);
			enable(#ORDERNO,false);
    		if($FD12!='0000'){
				showmsg(geterror());
				return(false);
			}
	}"

ITEM.ONCLICK:
    	"{
    		dim @ch as string;
			runoutput();
    		@ch=showmsg('下一步执行 客户联系方式录入，Y-继续;N-退出：');
			if(@ch == 'Y' or @ch == 'y'){	
					open('2105.XML', '#CUSTNO='+#CUSTNO+';');
					return(true);
				}
				else{
					return(true);
				}
    	}"

RESPONSE.PRINT:
