ITEM.ONCLICK:
    	"{
			//与神码通讯开始
			initfd();
			commsend_001();
			dim @baktxno as string;
			@baktxno=$TXNO;
			$FD16='7015';
			$FD28=#CUSTNO;
			$FD71=#LXTYPE;
			$FD95=#LXINFO;
			callagn();
			$TXNO=@baktxno;
			if($FD12!='0000')
			{
			showmsg(geterror());
			return(false);
			}
			//与神码通讯结束
    		dim @tota as string;
    		dim @ch as string;
			commsend_001();
			$FD25=#LXINFO;
			$FD27=#LXNAME;
			$FD29=#CUSTNO;
			$FD67=#LXTYPE;
    		@tota=callserver();
    		#ORDERNO=$FD48;
    		if($FD12!='0000'){
				showmsg(geterror());
				return(false);
			}
			else{
				showall();
				show(#SHOW,true);
				//showmsg(#ORDERNO);
				show(#ORDERNO,true);
				runoutput();		
    		@ch=showmsg('下一步执行 客户地址信息录入，Y-继续;N-退出：');
				if(@ch == 'Y' or @ch == 'y' ){	
						open('2106.XML', '#CUSTNO='+#CUSTNO+';');
						return(true);
					}
					else{
						return(true);
					}
					}
    		}"

