ITEM.ONLOSTFOCUS:
"{
  dim @txno_bak as string;
  @txno_bak=$TXNO;
  $TXNO='9942';
  $FD28=#CUSTNO;
  commsend_001();
  $TXNO=@txno_bak;
  callserver();
  #KHTYPE=$FD66;
  if($FD12!='0000'){
       showmsg(geterror());
       return(false);
         }
  if(#KHTYPE != '1')
   {
     showmsg('客户号只能为个人客户');
     return(false);
   }
}"

