ITEM.ONLOSTFOCUS:
  "{
   dim @txno_bak as string;
   @txno_bak=$TXNO;
   $TXNO='9942';
   $FD28=#CUSTNO;
   commsend_001();
   $TXNO=@txno_bak;
   callserver();
   #KHTYPE=$FD66;
   if($FD12 != '0000')
   {
        showmsg(geterror());
        return(false);
   }
   if(#KHTYPE == '1')
   {
        showmsg('不能输入个人客户号');
        return(false);
   }
   else if(#KHTYPE != '3')
   {
	showmsg('客户号不是机构客户号!');
        return(false);
   }
  }"

ITEM.ONCLICK:
    	"{
    		dim @tota as string;
    		dim @ch as string;
		commsend_001();
		$FD21=#BZTYPE;
		$FD28=#CUSTNO;
		$FD30=#ACTNO;
		$FD39=itoa(#CKBAL*100,'%016.0f');
		$FD40=itoa(#YHBAL*100,'%016.0f');
		$FD66=#CPTYPE;
		$FD67=#JGTYPE;
		$FD91=#BRNO;
    		@tota=callserver();
    		#ORDERNO=$FD35;
       		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
			}
		else{
			showall();
			show(#SHOW,true);
			//showmsg(#ORDERNO);
			show(#ORDERNO,true);
			runoutput();

					}
    		}"

