ITEM.ONCLICK:
    	"{
    		initfd();
    		dim @tota as string;
    		dim @ch as string;
				commsend_001();
				$FD26=#NAME;
				$FD28=#CUSTNO;
				$FD67=#GLLX;
				$FD69=#ZJLX;
				$FD62=#ZJHM;
				$FD37=#ZZJGDM;
				$FD38=#XYDM;
    		@tota=callserver();

       		if($FD12!='0000'){
				showmsg(geterror());
					return(false);
				}
				else{
				showall();
				runoutput();
    		@ch=showmsg('下一步执行 公司股东信息录入，Y-继续;N-退出：');
					if(@ch == 'Y' or @ch == 'y'){	
						open('2114.XML', '#CUSTNO='+#CUSTNO+';');
						return(true);
					}
					else{
						return(true);
					}
					}
    		}"

