TRANSACTION.COMMSEND:
"{
	//与神码通讯开始
	initfd();
	commsend_001();
	$FD16='7015';
	$FD28=#KHH;
	$FD71=#LXFS;
	$FD95=#LXXX;
	callagn();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
//	showmsg('与神码通讯成功结束');
	initfd();
	//与神码通讯结束
	commsend_001();
	$FD29=#KHH;
	$FD48=#XH;
	$FD68=#CZLX;
	$FD27=#LXR;
	$FD67=#LXFS;
	$FD62=#LXXX;
}"

ITEM.ONLOSTFOCUS:
 "{	initfd();	
    commsend_001();
 		$FD16='9894';
		$FD29=#KHH;
	  $FD48=#XH;
	  $FD68=#CZLX;
		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		#LXR=delspace($FD27);
		#LXFS=delspace($FD67);
		#LXXX=delspace($FD62);
		show(#LXR,true);
		show(#LXFS,true);
		show(#LXXX,true);
		if(#CZLX == '2')
		{
			goitem(#SUBMIT);
		}
}"

