TRANSACTION.COMMSEND:
"{
	//与神码通讯开始
	initfd();
	commsend_001();
	$FD16='7015';
	$FD28=#KHH;
	$FD26=#DWMC;
	callagn();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	//showmsg('与神码通讯成功结束');
	initfd();
	//与神码通讯结束
	commsend_001();
	$FD28=#KHH;
	$FD49=#GZXH;
	$FD67=#GZZT;
	$FD22=#HYDM;
	$FD69=#ZWDM;
	$FD68=#ZYDM;
	$FD26=#DWMC;
	$FD44=#RZRQ;
	$FD45=#LZRQ;
	$FD41=getitems('#NXSR');
	$FD70=#CZLX;
}"

