ITEM.ONLOSTFOCUS:
	   "{ 
	     initfd();
       	dim @txno as string;     
	     @txno = $TXNO;
	     $TXNO='9848';
	     $FD29=#CUSTNO;
	     commsend_001();
	     callserver();
	     $TXNO=@txno;
	     if($FD12!='0000')
	     {showmsg(geterror());
	      return(false);
	     }
       #CUSTNAME=delspace($FD25);
       show(#CUSTNAME,true);
       enable(#CUSTNAME,false);
       }"

	"{ initfd();
	   dim @txno as string;
           @txno = $TXNO;
           $TXNO='9850';
	   $FD92=#CUMACODE;
	   commsend_001();
	   callserver();
	   $TXNO=@txno;
     if($FD12!='0000'){
		 showmsg(geterror());
		 return(false);
       }
	   #CUMANAME=delspace($FD62);
	   show(#CUMANAME,true);
	   enable(#CUMANAME,false);
	   #CUMATYPE=delspace($FD70);
	   show(#CUMATYPE,true);
    }"

