ITEM.ONLOSTFOCUS:
 	    "{initfd();
 	  	  dim @txno_bak as string;
 	 	    dim @tota as string;
 	  	  @txno_bak=$TXNO;
 	 	    $TXNO='9849';
 	 	    $FD28=#CUSTNO;
 	   	  commsend_001();
 	  	  @tota=callserver();
 	  	  $TXNO=@txno_bak;
		    if($FD12!='0000'){
			  showmsg(geterror());
			  return(false);
		    } 
		    #CUSTNAME=delspace($FD26);
		    show(#CUSTNAME,true);
		    enable(#CUSTNAME,false);
		 }"

  "{
	commsend_001();
	$FD28=#CUSTNO;
	$FD26=#CUSTNAME;
	$FD67=#ADTYPE;
	$FD81=#JTINFO;
	$FD92=#TSOBJE;
	callserver();
	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}

	#SEQUNO=$FD48;
	show(#SHOW,true);
	show(#SEQUNO,true);
	enable(#SEQUNO,true);
	goitem(#PASSWD);
  }"

