ITEM.ONLOSTFOCUS:
	    "{initfd();
	      dim @txno_bak as string;
	      dim @tota as string;
	      @txno_bak=$TXNO;
	      $TXNO='9854';
	      $FD28=#CUSTNO;
	      $FD48=#SEQUNO;
	      commsend_001();
	      @tota=callserver();
	      $TXNO=@txno_bak;	
	      if($FD12!='0000')
	      {showmsg(geterror());
	      return(false);
	      }
	      #ADCONT=delspace($FD81);
	      show(#ADCONT,true);
	      #TSOBJE=delspace($FD92);
        show(#TSOBJE,true);
      }"

