TRANSACTION.COMMSEND:
"{
	//身份核查 如果此帐号标识为3年以及3年以上不动 则提醒去做2322 add by wudawei 20130219
	dim @tota as string;
	dim @result as string;
  		initfd();
  		commsend_001();
  		$FD16='9321'; 
  		$FD30=#ACTNO;
  		$FD67='1';
  		@tota=callserver();
  		if($FD12!='0000')
  		{
  			showmsg(geterror());
  			return(false);
  		}
  		@result=strfld(@tota,'|',11);
  		if( @result!='' and @result=='三年及三年以上不动户'){
				showmsg(''+@result);
			}
	//add by ck 20091122 调用卡系统的销户交易
	if(substr(#ACTNO,0,6) == '621223')
	{
		initfd();
		commsend_001();
		$FD16='7003';
		$FD30=#ACTNO;
		$FD79=#DRAW_PWD;
		$FD66=#CASHFLAG;
		$FD75=$MSR2;
		$FD76=$MSR3;
		$FD95=#ACCT_CLOSE;
		if(#XKTYPE=='2')
		{
		$FD58=#XKGSBH;//挂失编号
		}
		$FD70=#XKTYPE;
		callagn();
		if($FD12!='0000')
			{
				showmsg(geterror());
				return (false);
			}
                //showmsg('test');
	#TRAN_FEE = $FD39;
        #CARD_TRACENO = delspace(itoa(val($FD43),'%16d'));
        //showmsg(' #CARD_TRACENO ='+ #CARD_TRACENO );
	 }
	//卡卡转账销户(相当于卡存款) add by ck 20091122
	if((substr(#DFNO,0,6)=='621223') and (substr(#ACTNO,0,6) == '621223') and (#CASHFLAG=='2'))
	{
		initfd();
		commsend_001();
		$FD16='7004';
		$FD30=#DFNO;
		$FD39= itoa(val(#LTXAMT,100),'%016.2f');  // 交易金额
		callagn();
		//卡卡转账销户交易卡处理开户失败时，往数码发送卡销户撤销交易 add by ck 091122
		if($FD12!='0000')
		{
			#RESP_CODE=$FD12;
			showmsg(geterror());
			initfd();
			commsend_001();
			$FD16='7777';
			$FD12=#RESP_CODE;
			$FD30=#ACTNO;
			$FD39=itoa(#TXAMT*100);
			$FD43=#CARD_TRACENO;
			callagn();
			if($FD12!='0000')
			{
				showmsg(geterror());
				//return (false);
			}
			showmsg('卡销户失败!!...');
			rerun();
		}
		 #CARD_TRACENO_TRAN=delspace(itoa(val($FD43),'%16d'));
		 //#TRAN_FEE=$FD40;
 	}
 	//折卡转账销户(相当于卡存款) add by ck 20091122
 	else if ((substr(#ACTNO,0,6)!='621223') and (substr(#DFNO,0,6)=='621223')  and (#CASHFLAG=='2'))
 	{
 		initfd();
		commsend_001();
		$FD16='7004';
		$FD30=#DFNO;
		$FD39= itoa(val(#LTXAMT,100),'%016.2f');  // 交易金额
		callagn();
		//卡转账销户交易卡处理开户失败时，往数码发送卡销户撤销交易 add by ck 091122

		if($FD12!='0000')
		{
			showmsg(geterror());
			return (false);
		}
		 #CARD_TRACENO_TRAN=delspace(itoa(val($FD43),'%16d'));
		 //#TRAN_FEE=$FD40;
 	}

	//loadfds(@filename);	
	//与神码通讯 End by ck
	initfd();
	commsend_001();
	$FD12='2204';
	$FD16='9986';

	//#ACT_LX_CK=#ACT_LX;

	if(#VOCTYPE=='013' or #VOCTYPE=='014')
