ITEM.ONLOSTFOCUS:
"{
  	if('0' == #DQFS)
	{ enable(#ACTNO1,true);
		enable(#ACTNO2,false);
		enable(#AC_NO_NO,false);
		show(#ACTNO1,true);
		show(#ACTNO2,false);
		show(#AC_NO_NO,false);
		
	}
	else if('1' == #DQFS)
	{
	
		enable(#ACTNO1,false);
		enable(#ACTNO2,true);
		enable(#AC_NO_NO,false);
		show(#ACTNO1,false);
		show(#ACTNO2,true);
		show(#AC_NO_NO,false);
		
	}
	else if('2' == #DQFS)
	{
	
		enable(#ACTNO1,false);
		enable(#ACTNO2,false);
		enable(#AC_NO_NO,true);
		show(#ACTNO1,false);
		show(#ACTNO2,false);
		show(#AC_NO_NO,true);
	}
     refresh();
   }"

ITEM.ONCLICK:
"{
	dim @shuju as string;
	initfd();
	commsend_001();
	$FD16='7835';
	$FD30=#AC_NO;
	$FD40=itoa(#TX_AMT*100,'%012.0f');
	$FD43=#ORSEQNO;
	$FD75=$MSR2;
	$FD76=$MSR3; 
	$FD94=#DQFS;
	if(#DQFS=='2')
	{
	$FD94='0';
	}       
	if(#DQFS == '1')
	{
		@shuju=initiccard();
		#CARD_SEQ_ID=strfld(@shuju,'|',4);
		showmsg('#CARD_SEQ_ID='+#CARD_SEQ_ID);
		$FD23 ='0'+#CARD_SEQ_ID;//IC卡序列号
	}
	callagn();
	//showmsg('$FD12='+$FD12);
	if( $FD12=='H036' )
	{
		showmsg('原存款交易核心记账未成功，不能做存款确认交易！');
		return(false);
	}
	else if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	runoutput();
	rerun();
}"

RESPONSE.PRINT:
"{
	print( 5,30,'银联柜面通存款确认');
	print( 7,20,'交易日期:[8 ]',#TX_DATE);
	print( 8,20,'交易帐号:[20]',#AC_NO);
	print( 9,20,'平台流水:[12]',$FD52);
}"

