ITEM.ONLOSTFOCUS:
		  "{
		    //initfd();
		    dim @txno_bak as string;
 	 	    dim @tota as string;
 	  	  @txno_bak=$TXNO;
 	 	    $TXNO='9763';
 	 	    $FD31=#ACTNO;
 	   	  commsend_001();
 	  	  @tota=callserver();
 	  	  $TXNO=@txno_bak;
		    if($FD12!='0000'){
			  showmsg(geterror());
			  return(false);
			  }
			  #PAWDRAW=$FD67;
			  #CERDRAW=$FD68;
			  #CERTYPE=$FD69;
			  #FLAG=$FD71;
			  if(#PAWDRAW=='Y')
	          {show(#LDRAWPAW,true);
	           show(#DRAWPAW,true);}
	     if(#PAWDRAW=='N')
	          {show(#LDRAWPAW,false);
	           show(#DRAWPAW,false);} 
	     if(#CERDRAW=='Y')
	          {show(#LCERTYPE,true);
	           show(#CERTYPE,true);
	           show(#LCERNUM,true);
	           show(#CERNUM,true);} 
	     if(#CERDRAW=='N')
	          {show(#LCERTYPE,false);
	           show(#CERTYPE,false);
	           show(#LCERNUM,false);
	           show(#CERNUM,false);} 
			  showblock('DRAW',true,true);

			 }"

	      "{
		      dim @num as number;
		      @num = len(#CERNUM);
		     if('1' == #CERTYPE){
    		 if(@num == 15 or  @num == 18){
		     }else{
	  		 showmsg('身份证号码位数错误');
        	return(false);}  
    	   }
    	   }"

