    $FD12='2414';                   
    $FD67=#PAWDRAW;                 
    $FD68=#CERDRAW;                 
    $FD69=#CERTYPE;            
    $FD31=#ACTNO;                   
    $FD62=#CERNUM;                  
    $FD71=#FLAG;                     
   	$FD79=#DRAWPAW;
