ITEM.ONLOSTFOCUS:
  		"{
			   	dim @data as string;
					dim @temp1 as string;
					dim @temp2 as string;
					dim @selid as number;	
										   			
  			 	initfd();
					commsend_001();
					
					$FD16='9002';
					$FD18='CBS';
					$FD118=#REVBANKNAME;
					
					@data=callagn_cnaps2();
			 		if($FD12 != '0000')
					{
							showmsg(geterror());
							return(false);
					}					


   	 			autolist(@data);

					@selid=list_work();
					if(@selid < 0)
					{
						list_del();
						rerun();
						return(true);
					}
					@selid=list_getselid();
					if(@selid < 0)
					{
						list_del();
						rerun();
						return(true);
					}
	
					@temp1=ltrim(list_gettext(@selid,0));
					@temp2=ltrim(list_gettext(@selid,1));
					
					#REVBANKNO=@temp1;
					#REVBANKNAME=@temp2;
					
				  list_del();
  		}"

ITEM.ONCLICK:
 	 		"{
 	 		 		initfd();
					commsend_001();
					 	$FD18='CBS';
						$FD82=#PCNO;
						$FD16='3903';
						$FD38=#PJBNO;
						$FD40=#TXAMT;
						$FD76=#REVBANKNAME;
						$FD30=#REVBANKNO;
						$FD33=#REVACCNO;
						$FD101=#REVNAME;
						$FD100=#CONTENT;
						
					callagn_cnaps2();
			 		if($FD12 != '0000')
					{
							showmsg(geterror());
							return(false);
					}
					runoutput();
					close();
 	 		}"

