ITEM.ONLOSTFOCUS:
  "{
	dim @txno  as string;
	dim @tota  as string;
	dim @list1 as string;
	dim @list2 as string;
	dim @i     as number;

	@txno = $TXNO;
	$TXNO = '9950';
	$FD102=#ACTNO;
	commsend_001();
	@tota=callserver();
	$TXNO = @txno;
	if($FD12!='0000'){
	//	showmsg('帐号输入错误!');
		showmsg(geterror());
		return(false);
	}
	showblock('ACTINFO',true,true);
	setitems('#AVBALTOT',$FD40);                  //本息合计
	#AVBAL    = val(substr($FD102,185,16),0.01);  //本金
	#CUSTNAME = substr($FD102,125,60);
	#CUR      = substr($FD102,201,2);
	#PRDTNAME = substr($FD102,313,30);
	#VOCTYPE  = substr($FD102,28,3);
	#ACT_TYPE = substr($FD102,207,1);             //销户帐户类型
	//showmsg('销户帐户类型='+substr($FD102,197,1));
	#VOCNUM   = substr($FD102,31,16);
	show(#AVBALTOT,true);
	enable(#AVBALTOT,false);
	show(#AVBAL,true);
	enable(#AVBAL,false);
	show(#CUSTNAME,true);
	enable(#CUSTNAME,false);
	show(#CUR,true);
	enable(#CUR,false);
	show(#PRDTNAME,true);
	enable(#PRDTNAME,false);
	show(#PMFLAG,true);
	enable(#PMFLAG,false);
        if(#ACT_TYPE=='1')
	{
		while(substr(@tota, @i, 4)=='102L')
		{
		   	 @i = @i + 31;
		}
		@list1=substr(@tota, 0 , @i+1);
	 	@list2=substr(@tota, @i, len(@tota)-@i);
	 	setsel(#RESUME,@list1,'o5t20o1v3o2');
	 	setsel(#VOCTYPE,@list2,'o5t20o1v3o2');
        if(#ACT_TYPE!='2'){
		 	#VOCNUM='';		//不是定期那么就得输凭证
		}
		show(#VOCTYPE,true);
		enable(#VOCTYPE,true);
		show(#VOCNUM,true);
		enable(#VOCNUM,true);
	}
        else if(#ACT_TYPE=='2')
	{
	 	setsel(#RESUME,@tota,'o5t20o1v3o2');
		show(#VOCTYPE,true);
		enable(#VOCTYPE,false);
		//enable(#VOCNUM,false);
	}
  }"

