  "{
	dim @txno  as string;
	dim @tota  as string;
	dim @i     as number;
	dim @list1 as string;
	dim @list2 as string;

	if(#TRAN_ACTNO == #ACTNO)
	{
		showmsg('不能输入销户帐户本身!');
		return(false);
	}

	if(substr(#TRAN_ACTNO,0,3)=='407' or substr(#TRAN_ACTNO,0,3)=='416'){
		showmsg('不可以记此科目的帐务!');
		return(false);
	}
	if(substr(#TRAN_ACTNO,0,3)=='408'){
		if($KINBR!='10001'){
			showmsg('不可以记此科目的帐务!');
			return(false);
		}
	}
	@i = 0;
	initfd();
	@txno  = $TXNO;
	$FD12  = '2208';
	$TXNO  = '9730';
	$FD101 = #TRAN_ACTNO;

	commsend_001();
	@tota  = callserver();
	$TXNO  = @txno;

	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}

	#TRAN_NAME  = substr($FD101,74,60);
	#TRAN_CUR   = substr($FD101,150,2);
	#TRAN_AVBAL = val(substr($FD101,134,16),0.01);
	#TRAN_PNAME = substr($FD101,161,30);
