TRANSACTION.COMMSEND:
"{
		 initfd();
		 $MSGTYPE='03001';
		 dim @txno_bak as string;
		 @txno_bak=$TXNO;
		 $FD12='3103';
		 $TXNO='9986';
		 $FD25=#DKHM;
		 $FD34=#CD34;
		 $FD35=#CD35;
		 $FD37=#DKZH;
		 $FD39=space(16);
		 $FD40=itoa(#DKXYE*100, '%016.0f');
		 $FD41=itoa(#LJFFE*100, '%016.0f');
		 $FD42=itoa(#JHFKE*100, '%016.0f');
		 $FD50=#CD50;
		 $FD65='3103';
		 $FD67=#CZBZ;
		 $FD72=#CD72;
		 commsend_001();
		 callserver();
		 if($FD12!='0000')
			{
				showmsg(geterror());
				return(false);
			}
         $FD16='3103';
		 $MSGTYPE='03001';
    }"

ITEM.ONLOSTFOCUS:

  	   "{
		dim @txno as string;

		$FD37=#DKZH;
		$FD65='3103';
		@txno=$TXNO;
		$TXNO='9807';
		commsend_001();

		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
			}
		#DKHM=$FD25;
		#DKXYE=val($FD42, 0.01);
		#LJFFE=val($FD41, 0.01);
		#JHFKE=val($FD40, 0.01);
        #CD34='0000';
		#CD35='0001';
		#CD50='001';
		#CD72='N';


		enable(#DKHM, false);
		enable(#DKXYE, false);
		enable(#LJFFE, false);
		enable(#JHFKE, false);

  }"

