"{
dim @txno as string;
dim @tota as string;

		@txno=$TXNO;
		$TXNO='9874';
		commsend_001();

		@tota=callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		$TXNO=@txno;

		call('rm -rf ../tmp/'+$KINBR+$TTYNAME+'_4403_'+#DKZH);
	savefiletxt('../tmp/'+$KINBR+$TTYNAME+'_4403_'+#DKZH, @tota);
	editfile('../xml/LN_PAY_PLN.DLG','../tmp/'+$KINBR+$TTYNAME+'_4403_'+#DKZH);

	setvfile(true, true)

	call('mv ../tmp/'+$KINBR+$TTYNAME+'_4403_'+#DKZH+'  ../tmp/'+$KINBR+$TTYNAME);
	call('end['+'mv ../tmp/'+$KINBR+$TTYNAME+'_4403_'+#DKZH+'  '+$KINBR+$TTYNAME);

}"
