TRANSACTION.FIRSTWORK:
"{
  #PIC='';
  #PIC1='';
  show(#PIC,false);
  show(#PIC1,false);
  show(#LBLPIC,false);
  show(#LBLPIC1,false);
  enable(#SETPIC,false);
  enable(#SETPIC1,false);
}"

ITEM.ONLOSTFOCUS:
"{
	initfd();
	commsend_001();
	$FD10 = $TTYNAME;
	$FD37 = #CARDNO;
	$FD65 = '3205';
	$FD16 = '9807';
	callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return (false);
	}
	#USERNAME = $FD25 ;
}"

"{
    dim @txnobak as string;
    dim @len as number;
    @len = len(rtrim(#CARDNO));
    if(@len != 0 and @len != 3 and @len != 5 and @len != 7)
    {
      @txnobak = $TXNO;
      $TXNO = '9663';
      $FD30 = #CARDNO;
      commsend_001();
      callserver();
      if($FD12 !='0000')
      {
        show(#PIC,false);
        show(#PIC1,false);
        show(#LBLPIC,false);
        show(#LBLPIC1,false);
        enable(#SETPIC,false);
        enable(#SETPIC1,false);
      }
      else
      {
        show(#PIC,true);
        show(#PIC1,true);
        show(#LBLPIC,true);
        show(#LBLPIC1,true);
        enable(#SETPIC,true);
        enable(#SETPIC1,true);
      }
      $TXNO = @txnobak;
    }

}"

