TRANSACTION.FIRSTWORK:
"{
  #PIC='';
  #PIC1='';
  show(#PIC,false);
  show(#PIC1,false);
  show(#LBLPIC,false);
  show(#LBLPIC1,false);
  enable(#SETPIC,false);
  enable(#SETPIC1,false);
}"

ITEM.ONLOSTFOCUS:
 "{
		$MSGTYPE='03001';
        $TXNO='9807';
        $FD2=$OPNBR;
        $FD3=$KINBR;
        $FD5=$HDATE;
        $FD6='';
        $FD7=$TLRNO;
        $FD8='';
        $FD9='';
        $FD10=$TTYNAME;
        $FD16='9807';
        $FD17='';
        $FD37=#ACTNO;
        $FD65='3305';

        callserver();
        if($FD12!='0000')
        {
			showmsg(geterror());
            return(false);
        }

		#NAME=$FD25;
		#TXTP=$FD70;
		show(#NAME,true);
		show(#TXTP,true);
  }"

"{
    dim @txnobak as string;
    dim @len as number;
    @len = len(rtrim(#ACTNO));
    if(@len != 0 and @len != 3 and @len != 5 and @len != 7)
    {
      @txnobak = $TXNO;
      $TXNO = '9663';
      $FD30 = #ACTNO;
      commsend_001();
      callserver();
      if($FD12 !='0000')
      {
        show(#PIC,false);
        show(#PIC1,false);
        show(#LBLPIC,false);
        show(#LBLPIC1,false);
        enable(#SETPIC,false);
        enable(#SETPIC1,false);
      }
      else
      {
        show(#PIC,true);
        show(#PIC1,true);
        show(#LBLPIC,true);
        show(#LBLPIC1,true);
        enable(#SETPIC,true);
        enable(#SETPIC1,true);
      }
      $TXNO = @txnobak;
    }

}"

"{
  switch(#TXTP){
	case '1':
		#DESC='正常';
		break;
	case '2':
		#DESC='次级';
		break;
	case '3':
		#DESC='关注';
		break;
	case '4':
		#DESC='可疑';
		break;
	case '5':
		#DESC='损失';
		break;
  }
  }"

