      else
      {
        show(#PIC,true);
        show(#PIC1,true);
        show(#LBLPIC,true);
        show(#LBLPIC1,true);
        enable(#SETPIC,true);
        enable(#SETPIC1,true);
      }
      $TXNO = @txnobak;
    }

}"

