        $FD31=#ACTNO;
        $FD65='3309';

        callserver();
        if($FD12!='0000')
        {
			showmsg(geterror());
            return(false);
        }

		#NAME=$FD25;
		#CPBM=$FD23;
		#CPMC=$FD81;
		#OPDAY=$FD44;
		#ENDAY=$FD45;
		#ENDAY2=$FD45;
		#RATE=atoi($FD84)/1000000;
		#NOMRATE=atoi($FD84)/1000000;
		#SEQNO=$FD35;

		if(atoi($FD35)>1)
		{
			show(#S_SEQNO,true);
			show(#SEQNO,true);
		}

		show(#NAME,true);
		show(#CPBM,true);
		show(#CPMC,true);
		show(#ENDAY,true);
		show(#ENDAY2,true);
		show(#OPDAY,true);
		show(#RATE,true);
		show(#NOMRATE,true);
		enable(#NAME,false);
		enable(#CPBM,false);
		enable(#CPMC,false);
		enable(#OPDAY,false);
		enable(#ENDAY,false);
		enable(#ENDAY2,false);
		enable(#NOMRATE,false);

  }"

