ITEM.ONLOSTFOCUS:
  "{
  	dim @tota as string;
	dim @line as string;
  	dim @f as file;
  	dim @gaga_id as string;
  	dim @gaga_type as string;
  	dim @tot_amt as string;
	dim @tot_value as string;
	dim @name as string;
	dim @gaga_sts as string;
	dim @sts_name as string;
	dim @filename as string;
	dim @amt1 as number;
	dim @amt2 as number;

	list_clear();

	commsend_001();
	$FD16='4541';
	$FD65='4543';
	$FD37=#HTH;
	@tota=callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
        if($FD67 == '1')// 银承质押存单入库去质押存单做
        {
                open('5615.XML','#IMPAWNNUM='+#HTH+';');
                return (false);
        }
	@filename='../tmp/'+$KINBR+$TTYNAME;
	@f=openfile(@filename,'w');
	if(@f < 0)
	{
		showmsg('打开回传文件错误');
		return(false);
	}
	writestr(@f,@tota);
	closefile(@f);
	@f=openfile(@filename,'r');
	if(@f < 0)
	{
		showmsg('打开回传文件错误');
		return(false);
	}

	while(true)
	{
		@line=readline(@f);
		if(@line!='')
		{
			@gaga_id=strfld(@line,'|',0);
			@name=strfld(@line,'|',1);
			@amt1=val(strfld(@line,'|',2));
			@amt2=val(strfld(@line,'|',3));
			@sts_name=strfld(@line,'|',4);
		}else
		{
			break;
		}
		@tot_amt=ltrim(itoa(@amt1,'%17.2f'));  //认定价值
		@tot_value=ltrim(itoa(@amt2,'%17.2f'));//评估价值
		list_additem('',@gaga_id,@name,@tot_amt,@tot_value,@sts_name);
	}
	closefile(@f);
  	list_work();
  	#CPBAL = val($FD42,0.01);
  	#EV_BAL = val($FD43,0.01);

  	list_show();
  }"

  "{
    	dim @totamt as string;
  	dim @tmpamt as string;
  	@totamt = itoa( #CPAMT,'%17.2f' );
  	@tmpamt = itoa( #CPBAL,'%17.2f' );
	delspace(@totamt);
	delspace(@tmpamt);
  	if(#CPBAL != #CPAMT)
  	{
  		showmsg('输入总抵押金额与信贷审批金额'+'【'+@tmpamt+'】'+'不符!!');
