RESPONSE.PRINT:
"{
	$KINDNO = '00';
	dim @txtime  as string;
	dim @f as file;
	dim @line as string;
  	dim @gaga_id as string;
  	dim @gaga_type as string;
	dim @name as string;
	dim @filename as string;
	dim @amt1 as string;
	dim @amt2 as string;
	dim @sts_name as string;
	@txtime=substr($SYSTIME,0,2)+':'+substr($SYSTIME,2,2)+':'+substr($SYSTIME,4,2);
	@filename='../tmp/'+$KINBR+$TTYNAME;
	print( 4,12,'机构名称:[30]',$BRNAME);
	print( 4, 5,'交易代码:4543');
	print( 4, 5,'交易:抵质押物品出库');
	print( 5,12,'合 同 号:[20]',#HTH);
	print( 6,12,'抵质押总金额:[17]抵质押品总价值:[17]',ltrim(comma(itoa(#CPAMT,'%17.2f'))),ltrim(comma(itoa(#EV_AMT,'%17.2f'))));


	@f=openfile(@filename,'r');
	if(@f < 0)
	{
		showmsg('打开回传文件错误');
		return(false);
	}

	while(true)
	{
		@line=readline(@f);
		if(@line!='')
		{
			@gaga_id=strfld(@line,'|',0);
			@name=strfld(@line,'|',1);
			@amt1=val(strfld(@line,'|',2));
			@amt2=val(strfld(@line,'|',3));
			@sts_name=strfld(@line,'|',4);
		}else
		{
			break;
		}
		print(lineno()+1,12,'描述:[60]',@name);
		print(lineno()+1,16,'编号:([17])抵质押金额:[17]价值:[17]',@gaga_id,itoa(@amt1,'%17.2f'),itoa(@amt2,'%17.2f'),'%.2f'));
	}
	closefile(@f);
        printacdtl_1();
        print( 24,20,'备注:')
        print( 24,27,'交易日期:[8] [8]',$HDATE,@txtime);
        print( 24,5,'柜员:[4]',$FD7);
        print( 24,5,'流水号:[6]',$FD4);


}"
