ITEM.ONLOSTFOCUS:
"{
	 if(#JSFS == '0')
	 {
	 	#DQRQ ='';
	 	#SJSYL ='';
	 	show(#DQRQ,false); 
	 	show(#SJSYL,true); 
	 }
	 else if(#JSFS == '1')
	 {
	 	#DQRQ ='';
	 	#SJSYL ='';
	 	show(#DQRQ,true); 
	 	show(#SJSYL,true); 
	 }
	 else if(#JSFS == '2')
	 {
	 	#DQRQ ='';
	 	#SJSYL ='';
	 	show(#DQRQ,false); 
	 	show(#SJSYL,false); 
	 }
	 else if(#JSFS == '3')
	 {
	 	#DQRQ ='';
	 	#SJSYL ='';
	 	show(#DQRQ,true); 
	 	show(#SJSYL,false); 
	 }
}"

ITEM.ONCLICK:
"{
    dim @tota as string;
    dim @tmp_amt as string;
    initfd();
    commsend_001();
	  
    $FD16 = '8018';
    $FD65 = '0006';
    $FD66 = #JSFS;
    @tmp_amt=itoa(#SJSYL*1000000,'%08f');
    $FD44 = #DQRQ;    
    $FD73 = @tmp_amt;
    $FD63 = #CPDM;
    @tota = callserver();
	  if($FD12 != '0000')
	  {		
	    showmsg(geterror());
	    return(false);
	  }
	  savefiletxt('../tmp/LCSYYS'+$KINBR+$TTYNAME+'.scr',@tota);

	  	call('fview ../tmp/LCSYYS'+$KINBR+$TTYNAME+'.scr');
  }"
