"{
    dim @txnobak as string;
    dim @len as number;
    @len = len(rtrim(#CASH_AC_NO));
    if(@len != 0 and @len != 3 and @len != 5 and @len != 7)
    {
      @txnobak = $TXNO;
      $TXNO = '9663';
      $FD30 = #CASH_AC_NO;
      commsend_001();
      callserver();
      if($FD12 !='0000')
      {
        show(#PIC,false);
        show(#PIC1,false);
        show(#LBLPIC,false);
        show(#LBLPIC1,false);
        enable(#SETPIC,false);
        enable(#SETPIC1,false);
      }
      else
      {
        show(#PIC,true);
        show(#PIC1,true);
        show(#LBLPIC,true);
        show(#LBLPIC1,true);
        enable(#SETPIC,true);
        enable(#SETPIC1,true);
      }
      $TXNO = @txnobak;
    }

}"

  "{
  if (strfldcnt(#CONTENT,':') != 1 or strfldcnt(#CONTENT,'{') != 1 or strfldcnt(#CONTENT,'}') != 1) 
  {
  	showmsg('输入内容中不能含有半角:{}字符,请修改为全角其它字符');
	return(false);
  }
  }"

