"{
  dim @a     as string;
  dim @name  as string; //户名
  dim @avbal as string; //余额
  dim @PACNO as string;
  dim @prdt_name as string;//产品名称
  dim @actype as string;//帐户类型

  $MSGTYPE = '03001';
  commsend_001();
  $FD16    = '9731';
  $FD102    = #PAY_AC_NO;  

  @a = callserver();
  if($FD12 != '0000')
  {
    showmsg(geterror());
    return(false);
  }
  @name  = substr($FD102,125,60);
  @avbal = itoa(val(substr($FD102,185,16))/100,'%17.2f');
  @prdt_name=substr($FD102,313,30);
	@prdt_name=rtrim(@prdt_name);
	@actype=substr($FD102,207,1);
	if(@prdt_name!='应解汇款'){
		if(@actype!='1' and #PO_TYPE=='2' ){
			showmsg('［产品］'+@prdt_name+'不可以签发现金汇票');
			return(false);
		}
	}
  showhelp(4,58,5,20,'帐户信息','户名:'+@name+' 余额:'+@avbal);
  #PAY_NAME=rtrim(@name);
  show(#PAY_NAME,true);
  enable(#PAY_NAME,false);

}"

"{
    dim @txnobak as string;
    dim @len as number;
    @len = len(rtrim(#PAY_AC_NO));
    if(@len != 0 and @len != 3 and @len != 5 and @len != 7)
    {
      @txnobak = $TXNO;
      $TXNO = '9663';
      $FD30 = #PAY_AC_NO;
      commsend_001();
      callserver();
      if($FD12 !='0000')
      {
        show(#PIC,false);
        show(#PIC1,false);
        show(#LBLPIC,false);
        show(#LBLPIC1,false);
        enable(#SETPIC,false);
        enable(#SETPIC1,false);
      }
      else
      {
        show(#PIC,true);
        show(#PIC1,true);
        show(#LBLPIC,true);
        show(#LBLPIC1,true);
        enable(#SETPIC,true);
        enable(#SETPIC1,true);
      }
      $TXNO = @txnobak;
    }

}"

