ITEM.ONCLICK:
    	"{
        dim @tota as string;
    		dim @ch as string;
    		dim @bool as boolean;
    		commsend_001();
        $FD44=#STDATE;
        $FD45=#ENDATE;
        $FD91=#BRNO;
        @tota=callserver();
      if($FD12!='0000')
      {
			  showmsg(geterror());
			  return(false);
			}
		  else
		  {
		   showall();
		   @ch=showmsg('请选择打印方式：0:退出 1:主机 2:终端 ');
		   if(@ch == '0')
		   { autolist(@tota);list_work();
		     return(true);
		   }
		   else if(@ch == '1')
		   {
		   autolist(@tota);
		   list_work();
		   return(true);
		  }
		  else
		  {
		  autolist(@tota);
		  list_work();
		  list_save('../tmp/test.dat');
		  runoutput();
		   }
		  }
		}"

