ITEM.ONLOSTFOCUS:
"{dim @type as string;
  @type=#LSTYPE;
  switch(@type){
  case '1':	showblock('#SECODE',false,false); 
  						show(#LPCNO,true);
  						show(#PCNO,true);
  						return(true);
  case '2': showblock('#SECODE',false,false); 
  					 show(#LPCNO,true);
  					 show(#PCNO,true);
            return(true);
  case '3': show(#LPCNO,false);
            showblock('#SECODE',true,true);            
            return(true);
  case '4': show(#LPCNO,false);
            showblock('#SECODE',true,true);       
            return(true);
  case '5': showblock('#SECODE',false,false); 
  					 show(#LPCNO,true);
  					 show(#PCNO,true);
  					 return(true);
  case '6': show(#LPCNO,false);
            showblock('#SECODE',true,true);            
            return(true);
  default:  return(false);
  }
 }"

ITEM.ONCLICK:
    "{ dim @type as string;
       dim @tota as string;
       dim @selid as string;
	     @type=#LSTYPE;
	     switch(@type){
	       case '1': #STCODE=#PCNO;break;
	       case '2': #STCODE=#PCNO;break;
	       case '4': #STCODE=#PCNO;break;
	       default:  break;
	     }
	    commsend_001();
      $FD48=#STCODE;
      $FD49=#ENCODE;
      $FD67=#LSTYPE;
	    @tota=callserver();
		  if($FD12!='0000'){
		    showmsg(geterror());
		   return(false);}
		 if(autolist(@tota)){
			 @selid=list_work(); } 
		//runoutput();
   }"
