    		if(#LV_STS=='J' or #LV_STS=='K' or #LV_STS=='L' or #LV_STS=='M' or #LV_STS=='N')
		{ 
			@flag='0';
			@baktxno=$TXNO;
			$TXNO='9731';
			#AC_NO=substr(#CASH_AC_NO,0,24)+space(24-len(#CASH_AC_NO))+'';
