TRANSACTION.COMMSEND:
"{
  commsend_001();
  call('cp ../tmp/'+$KINBR+$TTYNAME+' ../tmp/'+$KINBR+$TTYNAME+'.backup');
  call_9986();
  call('mv ../tmp/'+$KINBR+$TTYNAME+'.backup ../tmp/'+$KINBR+$TTYNAME);
  #RTXAMT=itoa(#TX_AMT*100,'%016.0f');
  $FD39=#RTXAMT;
  $FD101=getitems('#ACTNO24#ACTSEQ#TXAMT#VOCTYPE#VOCNUM#DESC10#NBFLAG#CUSTNAME#AVBAL#CURCD#CASHFLAG#DSCPT#ACTTYPE#ACTSEQ_2#PRODNAME');
  #OPCD='10';
  lvsend_123();
  if(#TDMODE=='0'){
  	$FD102='10101';
  }else{
	$FD102=#ACTNO24;
  }
}"

TRANSACTION.FIRSTWORK:
"{
  #PIC='';
  #PIC1='';
  show(#PIC,false);
  show(#PIC1,false);
  show(#LBLPIC,false);
  show(#LBLPIC1,false);
  enable(#SETPIC,false);
  enable(#SETPIC1,false);
}"

ITEM.ONLOSTFOCUS:
"{
    dim @baktxno as string;
    if(#TDMODE=='0'){
      @baktxno=$TXNO;
      $TXNO='9406';
      $FD48='0008';
      commsend_001();
      callserver();//调用9406得到本机构的110内部帐号,赋值给了1201
      $TXNO=@baktxno;
      if($FD12!='0000')
      {
        showmsg(geterror());
        return (false);
      }
      #CASHACTNO19=substr($FD120,0,19);
      enable(#CASHACTNO19,false);
    }
    else{
      #CASHACTNO19='';
      enable(#CASHACTNO19,true);
    }
  }"

"{
