ITEM.ONLOSTFOCUS:
"{
		dim @selid as number;
		dim @totcnt as number;
		if(#DOLIST!=''){
		  while(true){
			list_work();
			@selid = list_getselid();	
			#TXNUM_NAME=delspace(list_gettext(@selid,0));
			#TX_DATE=delspace(list_gettext(@selid,1));
			#TX_ORDERNO=delspace(list_gettext(@selid,2));
			#TX_AC_BR_NO=delspace(list_gettext(@selid,3));
			#CX_AC_NO=delspace(list_gettext(@selid,4));
			#YD_BRF=delspace(list_gettext(@selid,5));
			#STAT_NAME=delspace(list_gettext(@selid,6));
			#BAL=delspace(list_gettext(@selid,7));
			if(msgbox('     打印此记录!     ','提示','ok|cancel')!=0){
				break;
			}
			runoutput();
			list_rmitem(@selid);	
			@totcnt=list_getrowcnt();
			if(@totcnt==0){
				msgbox('    查询打印完毕    '+chr(10)+'------------------');
				break;
			}
		  }
			list_del();
			#DOLIST='';
			return(true);
		}
  }"

ITEM.ONCLICK:
"{
	dim @tota as string;
  	commsend_001();
	$FD3 =$KINBR;
	$FD44=#WT_DATE;
	$FD52=#AC_BR_NO;
	$FD91=#TXNUM;
	$FD28=#PACKID;
	$FD72=#LW_IND;
	@tota=callserver();
	if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}
	if(autolist(@tota)){
		//list_work();
	}
	#DOLIST='1';
  	}"

