ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	dim @ldxml as string;
	dim @filename as string;
	dim @tota as string;
	dim @packid as string;
	dim @pay_qsno as string;
	dim @pack_date as string;
	dim @brno as string;

	if(#DOLIST !='')
	{
			@selid=list_work();
			if(@selid < 0)
			{
				list_del();
				return(true);
			}
			@selid=list_getselid();
			if(@selid < 0)
			{
				list_del();
				return(true);
			}
			initfd();
			commsend_001();
			@pack_date = rtrim(list_gettext(@selid,4));
			@brno	   = rtrim(list_gettext(@selid,0));
			open('LVUNPACKINFO.XML','#TX_BRNO='+@brno+';#TX_DATE='+#WT_DATE+';');


			return(true);
	}
	list_del();
	#DOLIST='';
}"

ITEM.ONCLICK:
"{
	dim @tota as string;
	dim @offset as number;
	dim @cnt as number;
	dim @packlen as number;
	dim @flag as string;
	dim @id as number;
	list_init(17,78,5,1);
	list_setcolcnt(6);
	list_setcol(0,'交易行',9);
	list_setcol(1,'顺序号',8);
	list_setcol(2,'交   易   金   额',20);
	list_setcol(3,'开户行( 付 )',14);
	list_setcol(4,'开户行( 收 )',14);
	list_setcol(5,'状    态',8);
	initfd();

	commsend_001();
	$FD44	=	#WT_DATE;
	$FD91 	= 	#BR_NO;

 	$FD16='5895';

	@tota=callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	if(substr(@tota,36,1)=='E')
	{
		showmsg(substr(@tota,36,5) +'  '+substr(@tota,44,40))
		list_del();
		return(true);
	}
	if(autolist(@tota))
	{
//		list_work();
	}

	#DOLIST='1';
	}"
