ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	dim @ldxml as string;
	dim @filename as string;
	dim @tota as string;
	dim @packid as string;
	dim @pay_qsno as string;
	dim @pack_date as string;

	if(#DOLIST !='')
	{
			@selid=list_work();
			if(@selid < 0)
			{
				list_del();
				rerun();
				return(true);
			}
			@selid=list_getselid();
                        if(@selid < 0)
                        {
                                list_del();
                                rerun();
                                return(true);
                        }

			initfd();
			commsend_001();
			@pack_date = rtrim(list_gettext(@selid,1));
			@packid = rtrim(list_gettext(@selid,2));
			@pay_qsno = rtrim(list_gettext(@selid,3));
			open('LVPACKINFO.XML','#PACKID='+@packid+';#PACK_DATE='+#WTDATE+';#PAY_QSNO='+@pay_qsno+';#LW_IND='+#BZT+';#PACK_DATE='+@pack_date+';');


			return(true);
	}
	list_del();
	#DOLIST='';
}"

"{
	if(#BZT=='2')
