ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	dim @ldxml as string;
	dim @filename as string;
	dim @tota as string;
	dim @CMTNO as string;
	if(#DOLIST !='')
	{
		while(true)
		{
			@selid=list_work();
			if(@selid < 0)
			{
				list_del();
				rerun();
				return(true);
			}
			initfd();
			commsend_001();
		}
	}
	list_del();
	#DOLIST='';
}"

ITEM.ONCLICK:
"{
	dim @tota as string;
	dim @offset as number;
	dim @cnt as number;
	dim @packlen as number;
	dim @flag as string;
	dim @id as number;
	list_init(17,78,5,1);
	list_setcolcnt(6);
	list_setcol(0,'帐           号',24);
	list_setcol(1,' 顺序号 ',8);
	list_setcol(2,'  户     名  ',50);
	initfd();

	commsend_001();
	$FD2 = #BR_NO;
	@tota=callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	if(substr(@tota,36,1)=='E')
	{
		showmsg(substr(@tota,36,5) +'  '+substr(@tota,44,40))
		list_del();
		return(true);
	}
	if(autolist(@tota))
	{
		list_work();
	}

	#DOLIST='1';
	}"
