ITEM.ONLOSTFOCUS:
  "{
		dim @selid as number;
		dim @filename as string;
		dim @tota as string;
		dim @wt_date as string;
		dim @packid as string;
		dim @orderno as string;
		dim @tx_type as string;
		dim @br_no as string;
		dim @txtype_content as string;
		dim @sts as string;

		dim @pay_ac_no as string;
		dim @cash_ac_no as string;
		dim @qs_no as string;
		dim @or_br_no as string;
		dim @re_content as string;
		dim @re_content2 as string;
		dim @xy_no as string;
		dim @file_name as string;
		dim @otherword as string;

		if(#DOLIST != '')
		{
			@selid=list_work();
      if(@selid < 0)
      {
          list_del();
          return(true);
      }
      @selid=list_getselid();
      if(@selid < 0)
      {
          list_del();
          return(true);
      }

		}
		list_del();
		#DOLIST='';
  }"

ITEM.ONCLICK:
  "{
		   dim @tota as string;
       dim @offset as number;
       dim @cnt as number;
       dim @packlen as number;
       dim @flag as string;
       dim @id as number;
       list_init(17,78,5,1);
       list_setcolcnt(7);
       list_setcol(0,'包    号',9);
       list_setcol(1,'交易序号',10);
       list_setcol(2,'委托日期',10);
       list_setcol(3,'业务类型',14);
       list_setcol(4,'交易状态',18);
       list_setcol(5,'原业务类型',12);
       list_setcol(6,'应答状态',12);
       initfd();

       commsend_001();

		   $FD16='5931';
		   $FD91=#BR_NO;
		   $FD44=#RQ;
		   @tota=callserver();
		   if($FD12!='0000')
		   {
		   	showmsg(geterror());
		   	return(false);
		   }
		   if(substr(@tota,36,1)=='E')
       {
               showmsg(substr(@tota,36,5) +'  '+substr(@tota,44,40))
               list_del();
               return(true);
       }
		   if(autolist(@tota))
		   {
		      #DOLIST='1';
		   }

  }"
