TRANSACTION.COMMSEND:
"{
  initfd();
  commsend_001();
  #OPTYPE='1';
  $FD98=#OPTYPE;     //查询或录入的标志
  $FD39 = itoa( #TX_AMT*100,'%016.0f');
  if(#RCP_STS=='00'){
  	call_9986();
  }
  else{
  	#FD9_FEE_IND='3';
  	$FD9=getitems('#FD9_AUTH_NO#FD9_AUTH_PWD#FD9_FEE_IND');
  }
  $FD44=$HDATE;
  $FD20='1';
  $FD21='01';         //币种
  $FD30=#ACTNO24;     //科目帐号
  $FD40=$FD39;        //交易金额
  $FD66='1';          //借贷标志
  $FD67='2';          //现转标志 1:现金 2: 转帐 
  $FD68='1';          //帐务类型 1:转帐 7:现金
  $FD93='9';          //检查是否要检查密码; 9 表示强行不检查密码
  $FD99='2';          
  #TXAMT15=itoa(#TX_AMT*100,'%016.0f');
  $FD102=getitems('#ACTNO24#ACTSEQ#VOCTYPE#VOCNUM#FETCHFLAG#PWDFLAG#QUE_PWD#DRAW_PWD#ID_TYPE#ID_NO#STAMPFLAG#PWDFLAG2#PWD16#PRINTDATE#TXAMT15#CERTTYPE#CUSTNAME#AVBAL#CURCD#CT_IND#DSCPT#ACTTYPE#ACTSEQ_2#ACCOUNTFLAG#INTEREST1#INTEREST2#INTEREST3#PAYAMOUNT#NEWVOCNUM#SPEFLAG#PRODNAME#ACTNO_24#EDUCREDIT#DESC10');
  lvsend_123();
if(#TXNUM=='30102'){
	  $FD58=substr(#ZPNUM,4,16);
  }
  #INTYPE='1';
  $FD23=#PZTYPE;     //票据类型
  $FD28=#RCP8;       //退票理由
  $FD37=#ZPNUM;      //支票号码
  $FD69=#OSTAT;      //回执标志
  $FD86=#SHRESUT;    //审核结果
  $FD99=#INTYPE;     //查询方式
 }"

TRANSACTION.COMMRECV:
"{
  commrecv_001();
  lvrecv_123();
  setitems('#FD9_AUTH_NO#FD9_AUTH_PWD#FD9_FEE_IND',$FD9);
  if($FD12!='0000' and $FD12!='0002'){
	showmsg(geterror());
	return(false);
  }
}"

