ITEM.ONLOSTFOCUS:
"{
    dim @flag as string;

    initfd();
		commsend_001();
		$FD61 = #NOTE_NO;
		$FD59 = #FIN_CODE;
		$FD58 = #BR_NO;
		$FD16 = '2493';
		callserver(); 
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		@flag=delspace($FD71);
		//showmsg('是否支付='+@flag);           
		if( @flag == '1' )
		{
			showmsg('该凭证已做支付，不能撤销!');
			return(false);
		}

	}"

ITEM.ONCLICK:
  "{
      dim @tota as string; 
      dim @filename as string; 
      initfd();
      commsend_001();
      $FD16=$TXNO;
      $FD58=#BR_NO;
      $FD59=#FIN_CODE;
      $FD68=#PAY_NOTE_TYPE;
      $FD60=#NOTE_NO;
      @tota=calltips();
      if( $FD12!='0000')
      {
          showmsg(geterror());
		      return(false);
      }
      @filename='../tmp/'+$KINBR+$TTYNAME+#TXNO;
      savefiletxt(@filename,@tota); 
      call('fview '+@filename);  

  }"
