ITEM.ONLOSTFOCUS:
 "{
   //ADD BY ZHGF
   dim @c as string;
   dim @d as string;
   dim @a as string;
   dim @b as string;
   dim @FullMsg as string;
   @FullMsg='';
   initfd();
   dim @baktxno as string;
   @baktxno=$TXNO;
   $TXNO='9410';//ZHGF 查询机构代码表
   $FD61=#TAXORGCODE;
   commsend_001();
   calltips();
   $TXNO=@baktxno;
   if($FD12!='0000'){
     showmsg(geterror());
     return (false);
   }
   @d = substr($FD95,12,60);
   @c = substr($FD95,72,1);
   @a = substr($FD95,73,1);
   @b = substr($FD95,74,1);
   @FullMsg='户名:'+@d+chr(10)+'类型:'+@c+chr(10)+'征收机关类型:'+@a+chr(10)+'机构级次:'+@b;
   showhelp(6,70,8,3,'征收机关基本信息',@FullMsg);
   #TAXORGTYPE=substr(@a,0,1);
   $FD20=@b;
}"

ITEM.ONCLICK:
"{ 
   dim @tota as string;
   commsend_001();
   $FD16='5542';
   $FD45=#OORDERNO;
   $FD62=#WBSQXH;
   $FD44=#OWTDATE;
   $FD37=#CORPCODE;
   $FD38=#TAXPAYCODE;
   $FD60=#TAXORGCODE;
   @tota=calltips();
   if($FD12!='0000'){
       showmsg(geterror());
       return(false);
   }
   if(autolist(@tota)){
       list_work();
   }
  //runoutput();
  //rerun();
       }"
