"{
		dim @tota as string;
		if(#DOLIST1!=''){
			initfd();
			commsend_001();
			$FD44=#DZ_DATE;
			$FD25=#DZUNIT;
			$FD28=#LASTSUCCCNT;
			$FD45=#LASTFAILCNT;
			$FD46=#MIDSUCCCNT;
			$FD47=#MIDFAILCNT;
			$FD72='1';
			//$FD91=#TX_BR_NO;
			$FD16=$TXNO;
			//@tota=callserver(); chengbo    20090619      callserver换成calltips
			@tota=calltips();
			if($FD12!='0000'){
				showmsg(geterror());
				return(false);
			}
			if(autolist(@tota)){
				list_work();
			}
			#DOLIST1='';
			goitem(#DOLIST);
			return(true);
		}
	}"

ITEM.ONCLICK:
"{
	dim @tota as string;
	initfd();
	commsend_001();
	$FD44=#DZ_DATE;
	//$FD91=#TX_BR_NO;
	$FD72='0';
	$FD16=$TXNO;
	//@tota=callserver(); chengbo    20090619      callserver换成calltips
	@tota=calltips();	
	if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}
	savefiletxt('../tmp/'+$KINBR+$TTYNAME+'.backup',@tota);
	#DOLIST='1';
	//if(autolist(@tota)){
	//}
  }"
