ITEM.ONLOSTFOCUS:
"{
    if(#DOLIST!=''){
        dim  @tota as string;
        dim  @selid as number;
        dim  @taxorgcode as string;
        dim  @wtdate as string;
        dim  @tranno  as string;
        dim  @filename  as string;
        dim  @flag  as number;
        @selid=list_work();
        @selid=list_getselid();
        if(@selid < 0){
            return(false);
        }
        @tranno=list_gettext(@selid,0);
        @taxorgcode=list_gettext(@selid,2);
        @wtdate=list_gettext(@selid,1);
        //showmsg(@tranno);
        //showmsg(#CFX_NO);
        //showmsg(#TAX_ORG_CODE);
        //@filename='CFX_SZDTL.XML';
        //@flag=msgbox('是否查询税种?','提示','yes|no');
        //if(@flag==0){
         //   open(@filename,'#TXBRNO='+#TX_BR_NO+';#ENTRUSTDATE='+@wtdate+';#TAXORGCODE='+@taxorgcode+';#TRANO='+@tranno+';#CFXNO='+#CFX_NO+';');
          //  return(true);
        if(@flag==1){
            list_del();
            rerun();
            return(true);
        }else{
            return(false);
        }
    }
    list_del();
    #DOLIST='';
    return(true);
  }"

ITEM.ONCLICK:
"{
    dim @tota as string;
    initfd();
    commsend_001();
    $FD44=#ENTRUST_DATE;
    $FD61=#TAX_ORG_CODE;
    $FD36=#CFX_NO;
    $FD98=#KSSTAT;
    $FD91=#TX_BR_NO;
    $FD16=$TXNO;
    @tota=calltips();    
    if($FD12 != '0000')
    {
       showmsg(geterror());
       return(false);
    }
    if(autolist(@tota))
    {
    }
    #DOLIST='1';
  }"
