TRANSACTION.COMMSEND:
"{
   // initfd();
    commsend_001();
    $FD44=#ENTRUSTDATE;     //委托日期
    $FD25=#SENDORGCODE;     //发起机构代码    
    $FD87=#VCNO;            //验证撤销序号

}"

ITEM.ONLOSTFOCUS:
  "{
  dim @selid        as number;
	dim @sendorgcode  as string;
	dim @entrustdate  as string;
	dim @vcno         as string;
	dim @vcsign       as string;
	dim @taxorgcode   as string;
	dim @taxpayname   as string;
	dim @taxpaycode   as string;
	dim @payopbkcode  as string;
	dim @paybkcode    as string;
	dim @payacct      as string;
	dim @protocolno   as string;
	dim @vcresult     as string;
	dim @addword      as string;

	if(#DOLIST == '1')
	{
	    @selid=list_work();
	    if(@selid < 0)
	    {
		     list_del();
		     rerun();
		     return(true);
	    }
      @selid=list_getselid();
      if(@selid < 0)
      {
         list_del();
         rerun();
         return(true);
      }

	    @sendorgcode=rtrim(list_gettext(@selid,0));
	    @entrustdate=rtrim(list_gettext(@selid,1));
	    @vcno=rtrim(list_gettext(@selid,2));
	    @vcsign=rtrim(list_gettext(@selid,3));
	    @taxorgcode=ltrim(list_gettext(@selid,4));
	    @taxpayname=rtrim(list_gettext(@selid,5));
	    @taxpaycode=ltrim(list_gettext(@selid,6));
	    @payopbkcode=rtrim(list_gettext(@selid,7));
	    @paybkcode=ltrim(list_gettext(@selid,8));	    
      @payacct=ltrim(list_gettext(@selid,9));
      @protocolno=ltrim(list_gettext(@selid,10));
      @vcresult=rtrim(list_gettext(@selid,11));
      @addword=rtrim(list_gettext(@selid,12));

	     //三方协议验证与撤销登记簿信息
	    open('5563_1.XML','#SENDORGCODE='+@sendorgcode+';#ENTRUSTDATE='+@entrustdate+';#VCNO='+@vcno+';#VCSIGN='+@vcsign+';#TAXORGCODE='+@taxorgcode+';#TAXPAYNAME='+@taxpayname+';#TAXPAYCODE='+@taxpaycode+';#PAYOPBKCODE='+@payopbkcode+';#PAYBKCODE='+@paybkcode+';#PAYACCT='+@payacct+';#PROTOCOLNO='+@protocolno+';#VCRESULT='+@vcresult+';#ADDWORD='+@addword+';'); 

		showfooter();
		return(true);
	}
	list_del();
	#DOLIST='';
  }"

ITEM.ONCLICK:
"{
	dim @tota as string;
	initfd();
	commsend_001();
	$FD44=#ENTRUSTDATE;     //委托日期    
	$FD87=#VCNO;            //验证撤销序号
	$FD91=#LRBRNO;
	// @tota=callserver();  chengbo 20090619 HS
	$FD26=#PAYNAME;
	$FD30=#PAYCODE;
	$FD27=#CFXNO;
	@tota=calltips();
	if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}

	autolist(@tota);	
	#DOLIST='1';
}"
